function payment_status($type = null, $color = false)
{
    if ($color) {
        $value = [
            0 => 'primary',
            1 => 'success',
            2 => 'danger'
        ];
    } else {
        $value = [
            0 => __('master.Pending'),
            1 => __('master.Paid'),
            2 => __('master.Canceled'),
        ];
    }
    return $value[$type] ?? $value;
}

function getLanguageName($lang)
{
    switch ($lang) {
        case 'ar':
            return "العربية";
        case 'en':
            return "English";
        case 'fr':
            return "French";
    }
}

function genratePassword($length = 5)
{
    $rand = '';
    for ($i = 0; $i < $length; $i++) {
        $rand .= rand(0, 9);
    }
    return $rand;
}

function l()
{
    return app()->getLocale();
}

function adminInfo()
{
    return User::role('SuperAdmin')->first()->store()->first()->information()->first();
}

if (!function_exists('active')) {

    function active($str)
    {
        $url = request()->url();
        $url = explode('/', $url);
        return in_array($str, $url) ? 'active' : '';
    }

}
