function renewSubScribe()
{
    $subs = Subscribe::with('store')->whereStatus(1)->orderBy('id', 'desc')->whereDate('deadline', '>', now())->whereDate('deadline', '<=', now()->addDays(10))->get();
    foreach ($subs as $item) {
        \Illuminate\Support\Facades\DB::beginTransaction();
        $plan = Plan::whereId($item->plan->id)->first();
        if ($plan->id != 4) {

            if (!$plan) {
                \Illuminate\Support\Facades\Log::info($item->id . " Has Wrong");
                continue;
            }
            $months = 1;
            $user = $item->store->users()->first();
            $store = $item->store;
            $merchant = $user;
            $status = false;
            if ((double)$merchant->wallet_total >= (double)$plan->price) {
                $pay = new MerchantWallet();
                $pay->amount = $plan->price;
                $pay->type_method = 'withdraw';
                $pay->type_operation = 1;
                $pay->status = 1;
                $pay->merchant()->associate($merchant);
                $pay->store()->associate($merchant->store);
                $pay->save();
                $status = 'success';
            }
            if ($status == 'success') {
                $pay = new PaymentTable();
                $pay->user()->associate($user);
                $pay->plan()->associate($plan);
                $pay['amount_total'] = $plan->price;
                $pay['discount'] = 0;
                $pay['type'] = 'Wallet';
                $pay->save();
                $subscribe = new Subscribe();
                $subscribe['deadline'] = $months ? date('Y-m-d h:i:s', strtotime($months . ' months ')) : now()->addMonth();
                $subscribe->payment()->associate($pay);
                $subscribe->store()->associate($store);
                $subscribe->plan()->associate($plan);
                if ($subscribe->save()) {
                    Subscribe::whereStore_id($store->id)->update(['status' => false]);
                    $subscribe->update(['status' => true]);
                };
                $user->update(['status' => true]);
                $store->update(['status' => true, 'plan_id' => $plan->id]);
                $message = 'payment_success';
                echo $item->id . " " . $message . "<br>";
                \Illuminate\Support\Facades\DB::commit();
            } else {
                \Illuminate\Support\Facades\Log::info($item->id . " Renew Successfully");
            }
        }
    }
}

function PhoneFormat($phone)
{
    return str_replace('+', '', $phone);
}
